div.script:
function _CheckValidNumForOriginals(ev) {
var isNumber = IsNumberOnly('input#number_of_originals');
if(!isNumber) {
document.getElementById("number_of_originals_div").innerHTML = "Please enter Number";
document.getElementById("number_of_originals_div").style.color="Red";
document.getElementById("number_of_originals").focus();
} else {
document.getElementById("number_of_originals_div").innerHTML = "";
}
}
function _CheckValidNumForCopies(ev) {
var isNumber = IsNumberOnly('input#number_of_copies');
if(!isNumber) {
document.getElementById("number_of_copies_div").innerHTML = "Please enter Number";
document.getElementById("number_of_copies_div").style.color="Red";
document.getElementById("number_of_copies").focus();
} else {
document.getElementById("number_of_copies_div").innerHTML = "";
}
}
function IsNumberOnly(element) {
var value = $(element).val();
var regExp = "^\\d+$";
return value.match(regExp);
}
